import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Sidebar = ({ user }) => {
  const [activeButton, setActiveButton] = useState("profile");

  const navigate = useNavigate();

  const handleButtonClick = (buttonName) => {
    setActiveButton(buttonName);
    if (buttonName === "profile") {
      navigate(`/home/profile`, { state: { user: user } });
    }
  };
  return (
    <>
      <div className="w-1/4 bg-gray-200 p-4 flex items-center justify-center flex-col">
        <div className="bg-gray-30 rounded-full w-32 h-32 flex items-center justify-center mb-2">
          <img
            src={require("../img/FOUR.PNG")}
            alt="Profile Picture"
            className="w-26 h-26 rounded-full"
          />
          <p className="text-center text-2xl font-bold">{user.username}</p>
        </div>
      </div>
    </>
  );
};

export default Sidebar;
