import React, { useState } from "react";
import { FaTrash, FaPencilAlt } from "react-icons/fa";

const Income = () => {
  const user_id = 1;
  const [title, setTitle] = useState("");
  const [amount, setAmount] = useState("");
  const [date, setDate] = useState("");
  const [note, setNote] = useState("");
  const [incomes, setIncomes] = useState([]);
  const [editIndex, setEditIndex] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);

  const calculateTotalIncome = () => {
    return incomes.reduce(
      (total, income) => total + parseFloat(income.amount),
      0
    );
  };

  const handleAddIncome = async () => {
    if (!title || !amount || !date) {
      return;
    }

    const newIncome = {
      user_id,
      title,
      amount,
      date,
      note,
    };

    try {
      const response = await fetch("http://localhost:8000/home/income/add", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newIncome),
      });

      const data = await response.json();
      const { income_id } = data;

      setIncomes([...incomes, { ...newIncome, income_id }]);
      setTitle("");
      setAmount("");
      setDate("");
      setNote("");
    } catch (error) {
      console.error("Error adding income:", error);
    }
  };

  const handleDeleteIncome = async (index) => {
    const incomeToDelete = incomes[index];
    console.log("Income to delete:", incomeToDelete);

    try {
      await fetch(
        `http://localhost:8000/home/income/remove/${incomeToDelete.income_id}/${user_id}`,
        {
          method: "DELETE",
        }
      );

      const updatedIncomes = [...incomes];
      updatedIncomes.splice(index, 1);
      setIncomes(updatedIncomes);
    } catch (error) {
      console.error("Error deleting income:", error);
    }
  };

  const handleEditIncome = (index) => {
    const incomeToEdit = incomes[index];
    setTitle(incomeToEdit.title);
    setAmount(incomeToEdit.amount);
    setDate(incomeToEdit.date);
    setNote(incomeToEdit.note);
    setEditIndex(index);
    setShowEditModal(true);
  };

  const handleSaveEdit = async () => {
    const editedIncome = {
      user_id,
      title,
      amount,
      date,
      note,
      income_id: incomes[editIndex].income_id,
    };

    try {
      await fetch("http://localhost:8000/home/income/update", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(editedIncome),
      });

      const updatedIncomes = [...incomes];
      updatedIncomes[editIndex] = editedIncome;
      setIncomes(updatedIncomes);

      setTitle("");
      setAmount("");
      setDate("");
      setNote("");
      setEditIndex(null);
      setShowEditModal(false);
    } catch (error) {
      console.error("Error updating income:", error);
    }
  };

  return (
    <div className="flex">
      {/* Left Side*/}
      <div className="w-1/3 p-4 border-r border-gray-300">
        <h2 className="text-4xl font-semibold mb-4">Add Income</h2>

        {/* Salary Title */}
        <div className="flex flex-col mb-4">
          <label className="mb-2 font-bold">Salary Title</label>
          <input
            type="text"
            className="border border-gray-300 rounded-lg px-3 py-2 mb-2"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </div>

        {/* Salary Amount */}
        <div className="flex flex-col mb-4">
          <label className="mb-2 font-bold">Salary Amount</label>
          <input
            type="text"
            className="border border-gray-300 rounded-lg px-3 py-2 mb-2"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
        </div>

        {/* Salary Date */}
        <div className="flex flex-col mb-4">
          <label className="mb-2 font-bold">Date</label>
          <input
            type="date"
            className="border border-gray-300 rounded-lg px-3 py-2 mb-2"
            value={date}
            onChange={(e) => setDate(e.target.value)}
          />
        </div>

        {/* Salary Note */}
        <div className="flex flex-col mb-4">
          <label className="mb-2">
            <span className="font-bold">Note </span>
            (Optional)
          </label>
          <textarea
            className="border border-gray-300 rounded-lg px-3 py-2 mb-2"
            value={note}
            onChange={(e) => setNote(e.target.value)}
          />
        </div>

        <button
          className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded"
          onClick={handleAddIncome}
        >
          + Add Income
        </button>
      </div>

      {/* Right Side*/}
      <div className="w-2/3 p-4">
        <h2 className="text-4xl font-semibold mb-4 text-center">
          Total Income: ${calculateTotalIncome().toFixed(2)}
        </h2>
        <h2 className="text-lg font-semibold mb-4 text-center">Income List</h2>

        {/* Display Income List*/}
        {incomes.map((income, index) => (
          <div
            key={index}
            className="mb-2 text-center bg-white rounded-lg shadow-md p-4 hover:bg-gray-50 relative"
          >
            <p>Title: {income.title}</p>
            <p>Amount: ${income.amount}</p>
            <p>Date: {income.date}</p>
            <p>Note: {income.note}</p>
            <div className="absolute top-0 right-0 mt-2 mr-2 flex">
              <FaPencilAlt
                className="edit-icon text-gray-400 cursor-pointer hover:text-blue-500 mr-2"
                onClick={() => handleEditIncome(index)}
              />
              <FaTrash
                className="trash-icon text-gray-400 cursor-pointer hover:text-red-500"
                onClick={() => handleDeleteIncome(index)}
              />
            </div>
          </div>
        ))}
      </div>

      {showEditModal && (
        <div className="fixed top-0 left-0 w-full h-full bg-gray-500 bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded-lg">
            <h2 className="text-xl font-semibold mb-4">Edit Income</h2>
            {/* Edit Income Form */}
            <div className="flex flex-col mb-4">
              <label className="mb-2 font-bold">Income Title</label>
              <input
                type="text"
                className="border border-gray-300 rounded-lg px-3 py-2 mb-2"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
              />
            </div>
            <div className="flex flex-col mb-4">
              <label className="mb-2 font-bold">Income Amount</label>
              <input
                type="text"
                className="border border-gray-300 rounded-lg px-3 py-2 mb-2"
                value={amount}
                onChange={(e) => setAmount(e.target.value)}
              />
            </div>
            <div className="flex flex-col mb-4">
              <label className="mb-2 font-bold">Date</label>
              <input
                type="date"
                className="border border-gray-300 rounded-lg px-3 py-2 mb-2"
                value={date}
                onChange={(e) => setDate(e.target.value)}
              />
            </div>
            <div className="flex flex-col mb-4">
              <label className="mb-2 font-bold">Note</label>
              <textarea
                className="border border-gray-300 rounded-lg px-3 py-2 mb-2"
                value={note}
                onChange={(e) => setNote(e.target.value)}
              />
            </div>
            <button
              className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded"
              onClick={handleSaveEdit}
            >
              Save
            </button>
            <button
              className="bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded ml-2"
              onClick={() => setShowEditModal(false)}
            >
              Cancel
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Income;
