import React, { useState } from "react";
import { FaTrash } from "react-icons/fa";

const Transactions = () => {
  const [transactions, setTransactions] = useState([]);

  const handleAddTransaction = (transaction) => {
    setTransactions([...transactions, transaction]);
  };

  const handleDeleteTransaction = (index) => {
    const updatedTransactions = [...transactions];
    updatedTransactions.splice(index, 1);
    setTransactions(updatedTransactions);
  };

  return (
    <div className="flex">
      {/* Display Transactions List */}
      <div className="w-full p-4">
        <h2 className="text-4xl font-semibold mb-4 text-center">
          Transactions History
        </h2>
        {/* Display Transaction List */}
        {transactions.map((transaction, index) => (
          <div
            key={index}
            className="mb-2 text-center bg-white rounded-lg shadow-md p-4 hover:bg-gray-50 relative"
          >
            {transaction.type === "income" ? (
              <>
                <p>Income Title: {transaction.title}</p>
                <p>Amount: ${transaction.amount}</p>
              </>
            ) : (
              <>
                <p>Expense Title: {transaction.title}</p>
                <p>Amount: ${transaction.amount}</p>
                <p>Category: {transaction.category}</p>
              </>
            )}
            <p>Date: {transaction.date}</p>
            <p>Note: {transaction.note}</p>
            {/* Trashcan icon */}
            <FaTrash
              className="trash-icon absolute top-0 right-0 mt-2 mr-2 text-gray-400 cursor-pointer hover:text-red-500"
              onClick={() => handleDeleteTransaction(index)}
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default Transactions;
