import React, { useState } from "react";
import HomeNavbar from "../components/HomeNavBar";

const Home = () => {
  const [activeButton, setActiveButton] = useState(null);

  const handleButtonClick = (buttonName) => {
    setActiveButton(buttonName);
  };
  return (
    <>
      <HomeNavbar />
      <div className="flex h-screen">
        <div className="w-1/4 bg-gray-200 p-4">
          <img
            src="FOUR.PNG"
            alt="Profile"
            className="w-20 h-20 rounded-full ml-40 mt-5"
          />
          <p className=" text-center">Username</p>
          <button
            className="w-full mt-40 py-4 px-4 mb-4 bg-blue-500 hover:bg-blue-600 text-white rounded font-bold"
            onClick={() => handleButtonClick("dashboard")}
          >
            Dashboard
          </button>
          <button
            className="w-full mt-10 py-4 px-4 mb-4 bg-blue-500 hover:bg-blue-600 text-white rounded font-bold"
            onClick={() => handleButtonClick("income")}
          >
            Income
          </button>
          <button
            className="w-full mt-10 py-4 px-4 mb-4 bg-blue-500 hover:bg-blue-600 text-white rounded font-bold"
            onClick={() => handleButtonClick("transactions")}
          >
            Transactions
          </button>
          <button
            className="w-full mt-10 py-4 px-4 mb-4 bg-blue-500 hover:bg-blue-600 text-white rounded font-bold"
            onClick={() => handleButtonClick("expenses")}
          >
            Expenses
          </button>
          {/* Add more buttons as needed */}
        </div>
        <div className="w-2/3 bg-white p-4">
          {activeButton === "dashboard" && <DashboardContent />}
          {activeButton === "income" && <IncomeContent />}
          {activeButton === "transactions" && <TransactionsContent />}
          {activeButton === "expenses" && <ExpensesContent />}
          {/* Add more content components as needed */}
        </div>
      </div>
    </>
  );
};

const DashboardContent = () => {
  return <div className="text-center">Dashboard Content</div>;
};

const IncomeContent = () => {
  return <div className="text-center">Income Content</div>;
};

const TransactionsContent = () => {
  return <div className="text-center">Transactions Content</div>;
};

const ExpensesContent = () => {
  return <div className="text-center">Expenses Content</div>;
};

export default Home;
