// Home.js
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import HomeNavbar from "../components/HomeNavBar";
import HomeLeft from "../components/HomeLeft";
import Expenses from "../components/Expenses";
import Budget from "../components/Budget";
import Income from "../componenets/Income";
import HomeRight from "../components/HomeRight";
import axios from "axios";

const Home = () => {
  const navigate = useNavigate();
  const [activeButton, setActiveButton] = useState("dashboard");
  const [user, setUser] = useState({});

  const handleButtonClick = (buttonName) => {
    setActiveButton(buttonName);
  };

  useEffect(() => {
    fetch("http://localhost:8000/userInfo", {
      method: "GET",
      credentials: "include",
    })
      .then((response) => {
        if (response.ok) {
          console.log("Response: ", response);
          //const data = response.json();
          return response.json();
        } else {
          console.error("Failed to fetch user data");
        }
      })
      .then((data) => {
        console.log("Data received: ", data);
        setUser(data);
      })
      .catch((error) => {
        console.error("Error fetching user data: ", error);
      });

    /*
    axios
      .get("/userInfo")
      .then((res) => {
        console.log("Helo");
        console.log(res);
      })
      .catch((err) => console.log(err));
    */
  }, [navigate]);

  return (
    <>
      <HomeNavbar />
      <div className="flex h-screen">
        {/* Left Side - Sidebar */}
        <HomeLeft user={user} handleButtonClick={handleButtonClick} />

        {/* Right Side - Content Area*/}
        <HomeRight activeButton={activeButton} />

        {/* Send user info to each of the components*/}
        {user && <Expenses user={user} />}
        {user && <Budget user={user} />}
        {user && <Income user={user} />}
      </div>
    </>
  );
};

export default Home;
