import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";

const Budget = () => {
  const user_id = 1;
  const [budgetText, setBudgetText] = useState(""); // To hold the budget input as text
  const [totalBudget, setTotalBudget] = useState(0); // Total budget in number
  const [isBudgetEditable, setIsBudgetEditable] = useState(true); // Whether the budget field is editable
  const [message, setMessage] = useState(""); // Feedback messages
  const [showUpdateModal, setShowUpdateModal] = useState(false); // Modal visibility
  const location = useLocation();
  const { user } = location.state || {};

  const handleSetBudget = async () => {
    const budget = parseFloat(budgetText); // Convert text to a floating-point number
    if (isNaN(budget)) {
      setMessage("Please enter a valid budget amount.");
      return;
    }

    try {
      const response = await fetch("http://localhost:8000/home/budget/add", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ user_id, amount: budget }),
      });
      const data = await response.json();
      setMessage(data.message); // Set the success message
      setTotalBudget(budget); // Set the total budget after adding it
      setIsBudgetEditable(false); // Grey out the budget field
    } catch (error) {
      setMessage(`Error: ${error.message}`); // Set error message
    }
  };

  const handleResetBudget = async () => {
    try {
      const response = await fetch("http://localhost:8000/home/budget/reset", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ user_id }),
      });
      const data = await response.json();
      setMessage(data.message); // Set success message
      setTotalBudget(0); // Reset total budget after removal
      setBudgetText(""); // Clear the text field
      setIsBudgetEditable(true);
    } catch (error) {
      setMessage(`Error: ${error.message}`); // Set error message
    }
  };

  return (
    <div className="container mx-auto mt-8">
      <h1 className="text-5xl font-semibold text-center">
        Budget Planner {user.user_first_name}
      </h1>
      <div className="mt-4">
        <label className="block text-lg font-bold">Enter Monthly Budget:</label>
        <input
          type="text" // Text-only input to avoid up/down arrows
          value={budgetText}
          onChange={(e) => setBudgetText(e.target.value)} // Handle text input
          className={`border rounded w-full p-8 text-center${
            isBudgetEditable ? "" : "bg-gray-200"
          }`} // Grey out when not editable
          disabled={!isBudgetEditable} // Disable input when not editable
          style={{ fontSize: "30px" }}
        />
      </div>
      <div className="mt-4 flex justify-between">
        <button
          onClick={handleSetBudget} // Set budget action
          className="bg-blue-500 text-white font-bold px-6 py-4 rounded mt-4"
          style={{ width: "150px" }}
          disabled={!isBudgetEditable} // Disable when not editable
        >
          Set Budget
        </button>

        <button
          onClick={handleResetBudget} // Delete budget action
          className="bg-red-500 text-white font-bold px-6 py-4 rounded mt-4"
          style={{ width: "150px" }}
        >
          Reset Budget
        </button>
      </div>
      <div className="mt-4 text-red-500">{message}</div>
      <div className="flex flex-col items-center">
        {" "}
        {/* Vertical alignment */}
        <h2 className="text-5xl font-bold text-center mt-8">
          {" "}
          {/* Center the heading */}
          Total Budget
        </h2>
        <h2 className="text-7xl font-bold text-center m-5">
          {" "}
          {/* Center the budget amount */}
          {totalBudget} {/* This will be centered below the label */}
        </h2>
      </div>
    </div>
  );
};

export default Budget;
