import React, { useState, useEffect } from "react";
import DefaultNav from "../components/DefaultNav";
import Sidebar from "../components/Sidebar";
import UserMangaModal from "../components/UserMangaModal";

const Manga = () => {
  const [user, setUser] = useState("");
  const [userMangaList, setUserMangaList] = useState([]);
  const [selectedManga, setSelectedManga] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    fetch("http://localhost:8000/userInfo", {
      method: "GET",
      credentials: "include",
    })
      .then((response) => {
        if (response.ok) {
          console.log("Response received: ", response);
          return response.json();
        }
      })
      .then((data) => {
        setUser(data);
      })
      .catch((error) => {
        console.error("Error fetching user data: ", error);
      });

    fetch("http://localhost:8000/usermanga/getAllUserManga", {
      method: "GET",
      credentials: "include",
    })
      .then((response) => {
        if (response.ok) {
          console.log("Response received: ", response);
          return response.json();
        }
      })
      .then((data) => {
        setUserMangaList(data);
      })
      .catch((error) => {
        console.error("Error fetching manga for specified user: ", error);
      });
  });

  const handleRemove = async (user_id, manga_id) => {
    try {
      const response = await fetch("http://localhost:8000/usermanga/remove", {
        method: "DELETE",
        credentials: "include",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ user_id, manga_id }),
      })
        .then((response) => {
          if (response.ok) {
            console.log("Response received: ", response);
            setUserMangaList(
              userMangaList.filter((manga) => manga.manga_id !== manga_id)
            );
            return response.json();
          }
        })
        .catch((error) => {
          console.error("Error removing manga from user list: ", error);
        });
    } catch (error) {
      console.error("Error removing manga from user list (frontend): ", error);
    }
  };

  const handleRatingUpdate = () => {
    try {
    } catch (error) {}
  };

  const handleReadingStatusUpdate = () => {
    try {
    } catch (error) {}
  };

  const handleMangaClick = (manga) => {
    setSelectedManga(manga);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedManga(null);
  };

  return (
    <div className="flex flex-col h-screen">
      <DefaultNav />
      <div className="flex flex-1">
        <Sidebar user={user} />
        <div className="flex-1 bg-gray-700">{/* Main content goes here */}</div>
      </div>
    </div>
  );
};
export default Manga;
