// Home.js
import React, { useState, useEffect } from "react";
import HomeNavbar from "../components/HomeNavBar";
import HomeLeft from "../components/HomeLeft";
import HomeRight from "../components/HomeRight";

const Home = () => {
  const [activeButton, setActiveButton] = useState("dashboard");
  const [user, setUser] = useState({});

  useEffect(() => {
    // Fetch user data when the component mounts
    fetch("/home", {
      method: "GET",
      credentials: "include", // Include credentials (cookies) in the request
    })
      .then((response) => {
        if (response.ok) {
          return response.json(); // Parse response JSON if the request is successful
        } else {
          throw new Error("Failed to fetch user data");
        }
      })
      .then((data) => {
        setUser(data); // Set user data received from the backend
      })
      .catch((error) => {
        console.error("Error fetching user data:", error);
        // Handle the error, such as setting an error state or redirecting to a login page
      });
  }, []);

  const handleButtonClick = (buttonName) => {
    setActiveButton(buttonName);
  };

  return (
    <>
      <HomeNavbar />
      <div className="flex h-screen">
        {/* Left Side - Sidebar */}
        <HomeLeft user={user} handleButtonClick={handleButtonClick} />

        {/* Right Side - Content Area*/}
        <HomeRight activeButton={activeButton} />
      </div>
    </>
  );
};

export default Home;
