import React, { useState } from "react";
import Button from "../components/Button";
import Navbar from "../components/Navbar";

function Signup() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const handleSignup = (e) => {
    e.preventDefault();
    if (password != confirmPassword) {
      alert("Passwords do not match!");
      return;
    }
    //
  };

  return (
    <>
      <Navbar />
      <div className="max-w-2xl mx-auto mt-20 p-6">
        <h1 className="text-2xl font-bold mb-8">Sign Up</h1>
        <form onSubmit={handleSignup}>
          <div className="mb-8">
            <label
              className="block text-black-700 text-sm font-bold mb-2"
              htmlFor="username"
            >
              Username
            </label>
            <input
              className="border border-gray-300 rounded-lg px-3 py-2 w-full focus:outline-none focus:border-blue-500"
              type="text"
              id="username"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div className="mb-8">
            <label
              className="block text-black-700 text-sm font-bold mb-2"
              htmlFor="password"
            >
              Password
            </label>
            <input
              className="border border-gray-300 rounded-lg px-3 py-2 w-full focus:outline-none focus:border-blue-500"
              type="password"
              id="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <div className="mb-8">
            <label
              className="block text-black-700 text-sm font-bold mb-2"
              htmlFor="confirmpassword"
            >
              Confirm Password
            </label>
            <input
              className="border border-gray-300 rounded-lg px-3 py-2 w-full focus:outline-none focus:border-blue-500"
              type="password"
              id="confirmPassword"
              placeholder="Confirm Password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
            />
          </div>
          <Button
            type="submit"
            to={"/login"}
            className="bg-blue-500 hover:bg-blue-600 active:bg-blue-300 text-white font-bold py-3 px-6 rounded"
          >
            Sign Up
          </Button>
        </form>
      </div>
    </>
  );
}

export default Signup;
