import React, { useState } from "react";
import { FaTrash, FaPencilAlt } from "react-icons/fa";
import { useLocation } from "react-router-dom";

const Expenses = () => {
  const user_id = 1;
  const [title, setTitle] = useState("");
  const [amount, setAmount] = useState("");
  const [date, setDate] = useState("");
  const [note, setNote] = useState("");
  const [category, setCategory] = useState("");
  const [expenses, setExpenses] = useState([]);
  const [editIndex, setEditIndex] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);
  const location = useLocation();
  const { user } = location.state || {};

  const commonCategories = [
    "Education",
    "Entertainment",
    "Food",
    "Healthcare",
    "Transportation",
    "Utilities",
  ];

  const calculateTotalExpense = () => {
    return expenses.reduce(
      (total, expense) => total + parseFloat(expense.amount),
      0
    );
  };

  const handleAddExpense = async () => {
    if (!title || !amount || !date || !category) {
      return;
    }

    const newExpense = {
      user_id,
      title,
      date,
      amount,
      note,
      cat_name: category,
    };

    try {
      const response = await fetch("http://localhost:8000/home/expenses/add", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newExpense),
      });

      const data = await response.json();
      const { expense_id } = data;

      setExpenses([...expenses, { ...newExpense, expense_id }]);
      setTitle("");
      setAmount("");
      setDate("");
      setNote("");
      setCategory("");
    } catch (error) {
      console.error("Error adding expense:", error);
    }
  };

  const handleDeleteExpense = async (index) => {
    const expenseToDelete = expenses[index];
    console.log("Expense to delete: ", expenseToDelete);

    try {
      await fetch(
        `http://localhost:8000/home/expenses/remove/${expenseToDelete.expense_id}/${user_id}`,
        {
          method: "DELETE",
        }
      );

      const updatedExpenses = [...expenses];
      updatedExpenses.splice(index, 1);
      setExpenses(updatedExpenses);
    } catch (error) {
      console.error("Error deleting expense:", error);
    }
  };

  const handleEditExpense = (index) => {
    const expenseToEdit = expenses[index];
    setTitle(expenseToEdit.title);
    setAmount(expenseToEdit.amount);
    setDate(expenseToEdit.date);
    setNote(expenseToEdit.note);
    setCategory(expenseToEdit.cat_name);
    setEditIndex(index);
    setShowEditModal(true);
  };

  const handleSaveEdit = async () => {
    const editedExpense = {
      user_id,
      title,
      amount,
      date,
      note,
      cat_name: category,
      expense_id: expenses[editIndex].expense_id,
    };

    try {
      await fetch("http://localhost:8000/home/expenses/update", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(editedExpense),
      });

      const updatedExpenses = [...expenses];
      updatedExpenses[editIndex] = editedExpense;
      setExpenses(updatedExpenses);

      setTitle("");
      setAmount("");
      setDate("");
      setNote("");
      setCategory("");
      setEditIndex(null);
      setShowEditModal(false);
    } catch (error) {
      console.error("Error updating expense:", error);
    }
  };

  return (
    <div className="flex">
      {/* Left Side*/}
      <div className="w-1/3 p-4 border-r border-gray-300">
        <h2 className="text-4xl font-semibold mb-4">Add Expense</h2>

        {/* Expense Title */}
        <div className="flex flex-col mb-4">
          <label className="mb-2 font-bold">Expense Title</label>
          <input
            type="text"
            className="border border-gray-300 rounded-lg px-3 py-2 mb-2"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </div>

        {/* Expense Amount */}
        <div className="flex flex-col mb-4">
          <label className="mb-2 font-bold">Expense Amount</label>
          <input
            type="text"
            className="border border-gray-300 rounded-lg px-3 py-2 mb-2"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
        </div>

        {/* Expense Category */}
        <div className="flex flex-col mb-4">
          <label className="mb-2 font-bold">Category</label>
          <select
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            className="border border-gray-300 rounded-lg px-3 py-2 mb-2"
          >
            <option value="">Select Category</option>
            {commonCategories.map((cat, index) => (
              <option key={index} value={cat}>
                {cat}
              </option>
            ))}
          </select>
        </div>

        {/* Expense Date */}
        <div className="flex flex-col mb-4">
          <label className="mb-2 font-bold">Date</label>
          <input
            type="date"
            className="border border-gray-300 rounded-lg px-3 py-2 mb-2"
            value={date}
            onChange={(e) => setDate(e.target.value)}
          />
        </div>
        {/* Expense Note */}
        <div className="flex flex-col mb-4">
          <label className="mb-2">
            <span className="font-bold">Note </span>
            (Optional)
          </label>
          <textarea
            className="border border-gray-300 rounded-lg px-3 py-2 mb-2"
            value={note}
            onChange={(e) => setNote(e.target.value)}
          />
        </div>

        <button
          className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded"
          onClick={handleAddExpense}
        >
          + Add Expense
        </button>
      </div>

      {/* Right Side*/}
      <div className="w-2/3 p-4">
        <h2 className="text-4xl font-semibold mb-4 text-center">
          Total Expense: ${calculateTotalExpense().toFixed(2)}
        </h2>
        <h2 className="text-lg font-semibold mb-4 text-center">Expense List</h2>

        {/* Display Expense List*/}
        {expenses.map((expense, index) => (
          <div
            key={index}
            className="mb-2 text-center bg-white rounded-lg shadow p-4 hover:bg-gray-50 relative"
          >
            <p>Title: {expense.title}</p>
            <p>Amount: ${expense.amount}</p>
            <p>Category: {expense.cat_name}</p>
            <p>Date: {expense.date}</p>
            <p>Note: {expense.note}</p>
            <div className="absolute top-0 right-0 mt-2 mr-2 flex">
              <FaPencilAlt
                className="edit-icon text-gray-400 cursor-pointer hover:text-blue-500 mr-2"
                onClick={() => handleEditExpense(index)}
              />
              <FaTrash
                className="trash-icon text-gray-400 cursor-pointer hover:text-red-500"
                onClick={() => handleDeleteExpense(index)}
              />
            </div>
          </div>
        ))}
      </div>

      {showEditModal && (
        <div className="fixed top-0 left-0 w-full h-full bg-gray-500 bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded-lg">
            <h2 className="text-xl font-semibold mb-4">Edit Expense</h2>
            {/* Edit Expense Form */}
            <div className="flex flex-col mb-4">
              <label className="mb-2 font-bold">Expense Title</label>
              <input
                type="text"
                className="border border-gray-300 rounded-lg px-3 py-2 mb-2"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
              />
            </div>
            <div className="flex flex-col mb-4">
              <label className="mb-2 font-bold">Expense Amount</label>
              <input
                type="text"
                className="border border-gray-300 rounded-lg px-3 py-2 mb-2"
                value={amount}
                onChange={(e) => setAmount(e.target.value)}
              />
            </div>
            <div className="flex flex-col mb-4">
              <label className="mb-2 font-bold">Category</label>
              <select
                value={category}
                onChange={(e) => setCategory(e.target.value)}
                className="border border-gray-300 rounded-lg px-3 py-2 mb-2"
              >
                <option value="">Select Category</option>
                {commonCategories.map((cat, index) => (
                  <option key={index} value={cat}>
                    {cat}
                  </option>
                ))}
              </select>
            </div>
            <div className="flex flex-col mb-4">
              <label className="mb-2 font-bold">Date</label>
              <input
                type="date"
                className="border border-gray-300 rounded-lg px-3 py-2 mb-2"
                value={date}
                onChange={(e) => setDate(e.target.value)}
              />
            </div>
            <div className="flex flex-col mb-4">
              <label className="mb-2 font-bold">Note</label>
              <textarea
                className="border border-gray-300 rounded-lg px-3 py-2 mb-2"
                value={note}
                onChange={(e) => setNote(e.target.value)}
              />
            </div>
            <button
              className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded"
              onClick={handleSaveEdit}
            >
              Save
            </button>
            <button
              className="bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded ml-2"
              onClick={() => setShowEditModal(false)}
            >
              Cancel
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Expenses;
