import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const HomeLeft = ({ user }) => {
  const [activeButton, setActiveButton] = useState("dashboard");

  const navigate = useNavigate();

  const handleButtonClick = (buttonName) => {
    setActiveButton(buttonName);
    if (buttonName === "dashboard") {
      navigate(`/home/dashboard`, { state: { user: user } });
    } else if (buttonName === "budget") {
      navigate(`/home/budget`);
    } else if (buttonName === "expenses") {
      navigate(`/home/expenses`);
    } else {
      navigate(`/home/income`);
    }
  };

  return (
    <div className="w-1/4 bg-gray-200 p-4 flex items-center justify-center flex-col">
      {/* Profile*/}
      <div className="bg-gray-300 rounded-full w-32 h-32 flex items-center justify-center mb-2">
        <img
          src={require("../img/legodrake.png")}
          alt="Profile-Picture"
          className="w-26 h-26 rounded-full"
        />
      </div>
      <p className="text-center text-2xl font-bold">
        {user.user_first_name} {user.user_last_name}
      </p>

      {/* Redirect to Dashboard */}
      <button
        className={`w-40 mt-10 py-2 px-2 mb-4 rounded font-bold ${
          activeButton === "dashboard"
            ? "bg-blue-600"
            : "bg-blue-500 hover:bg-blue-600"
        } text-white`}
        onClick={() => handleButtonClick("dashboard")}
      >
        Dashboard
      </button>

      {/* Redirect to Budget */}
      <button
        className={`w-40 mt-6 py-2 px-2 mb-4 rounded font-bold ${
          activeButton === "budget"
            ? "bg-blue-600"
            : "bg-blue-500 hover:bg-blue-600"
        } text-white`}
        onClick={() => handleButtonClick("budget")}
      >
        Budget
      </button>

      {/* Redirect to Expenses */}
      <button
        className={`w-40 mt-6 py-2 px-4 mb-4 rounded font-bold ${
          activeButton === "expenses"
            ? "bg-blue-600"
            : "bg-blue-500 hover:bg-blue-600"
        } text-white`}
        onClick={() => handleButtonClick("expenses")}
      >
        Expenses
      </button>

      {/* Redirect to Income */}
      <button
        className={`w-40 mt-6 py-2 px-4 mb-4 rounded font-bold ${
          activeButton === "income"
            ? "bg-blue-600"
            : "bg-blue-500 hover:bg-blue-600"
        } text-white`}
        onClick={() => handleButtonClick("income")}
      >
        Income
      </button>
    </div>
  );
};

export default HomeLeft;
