import React, { useState } from "react";
import { FaTrash } from "react-icons/fa";

const Expenses = () => {
  const [title, setTitle] = useState("");
  const [amount, setAmount] = useState("");
  const [date, setDate] = useState("");
  const [note, setNote] = useState("");
  const [category, setCategory] = useState("");
  const [expenses, setExpenses] = useState([]);

  const commonCategories = [
    "Food",
    "Transportation",
    "Utilities",
    "Entertainment",
    "Healthcare",
    "Education",
  ];

  const calculateTotalExpense = () => {
    return expenses.reduce(
      (total, expense) => total + parseFloat(expense.amount),
      0
    );
  };

  const handleAddExpense = () => {
    const newExpense = {
      title: title,
      amount: amount,
      date: date,
      note: note,
      category: category,
    };

    setExpenses([...expenses, newExpense]);
    setTitle("");
    setAmount("");
    setDate("");
    setNote("");
    setCategory("");
  };

  const handleDeleteExpense = (index) => {
    const updatedExpenses = [...expenses];
    updatedExpenses.splice(index, 1);
    setExpenses(updatedExpenses);
  };

  return (
    <div className="flex">
      {/* Left Side*/}
      <div className="w-1/3 p-4 border-r border-gray-300">
        <h2 className="text-4xl font-semibold mb-4">Add Expense</h2>

        {/* Expense Title */}
        <div className="flex flex-col mb-4">
          <label className="mb-2 font-bold">Expense Title</label>
          <input
            type="text"
            className="border border-gray-300 rounded-lg px-3 py-2 mb-2"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </div>

        {/* Expense Amount */}
        <div className="flex flex-col mb-4">
          <label className="mb-2 font-bold">Expense Amount</label>
          <input
            type="text"
            className="border border-gray-300 rounded-lg px-3 py-2 mb-2"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
        </div>

        {/* Expense Category */}
        <div className="flex flex-col mb-4">
          <label className="mb-2 font-bold">Category</label>
          <select
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            className="border border-gray-300 rounded-lg px-3 py-2 mb-2"
          >
            <option value="">Select Category</option>
            {commonCategories.map((cat, index) => (
              <option key={index} value={cat}>
                {cat}
              </option>
            ))}
          </select>
        </div>

        {/* Expense Date */}
        <div className="flex flex-col mb-4">
          <label className="mb-2 font-bold">Date</label>
          <input
            type="date"
            className="border border-gray-300 rounded-lg px-3 py-2 mb-2"
            value={date}
            onChange={(e) => setDate(e.target.value)}
          />
        </div>
        {/* Expense Note */}
        <div className="flex flex-col mb-4">
          <label className="mb-2 font-bold">Note</label>
          <textarea
            className="border border-gray-300 rounded-lg px-3 py-2 mb-2"
            value={note}
            onChange={(e) => setNote(e.target.value)}
          />
        </div>

        <button
          className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded"
          onClick={handleAddExpense}
        >
          + Add Expense
        </button>
      </div>

      {/* Right Side*/}
      <div className="w-2/3 p-4">
        <h2 className="text-4xl font-semibold mb-4 text-center">
          Total Expense: ${calculateTotalExpense().toFixed(2)}
        </h2>
        <h2 className="text-lg font-semibold mb-4 text-center">Expense List</h2>

        {/* Display Expense List*/}
        {expenses.map((expense, index) => (
          <div
            key={index}
            className="mb-2 text-center bg-white rounded-lg shadow p-4 hover:bg-gray-50 relative"
          >
            <p>Title: {expense.title}</p>
            <p>Amount: ${expense.amount}</p>
            <p>Category: {expense.category}</p>
            <p>Date: {expense.date}</p>
            <p>Note: {expense.note}</p>
            {/* Trashcan icon */}
            <FaTrash
              className="trash-icon absolute top-0 right-0 mt-2 mr-2 text-gray-400 cursor-pointer hover:text-red-500"
              onClick={() => handleDeleteExpense(index)}
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default Expenses;
