import React from "react";
import { useLocation } from "react-router-dom";


const Dashboard = ({ totalIncome, totalExpenses, remainingBudget }) => {
  const location = useLocation();
  const { user } = location.state || {};

  // Check if user data exists
  if (!user) {
    // Handle the case where user data is not available
    return <div>Loading...</div>; // or any other handling mechanism
  }

  return (
    <div className="flex flex-col p-6">
      {/* Header */}
       <h1>Welcome, {user.user_first_name}!</h1>
      <header className="flex justify-center items-center mb-6">
        {" "}
        {/* Ensure horizontal centering */}
        <h1 className="text-5xl font-semibold">
          {" "}
          {/* No need for explicit text-center */}
          Expense Tracker Dashboard
        </h1>
      </header>
      {/* Overview Section */}
      <div className="flex justify-around mb-6">
        {" "}
        {/* Centered summary metrics */}
        <div className="bg-blue-500 text-white p-4 rounded-lg text-center">
          <h2 className="text-xl font-bold w-72">Total Income</h2>
          <p className="text-2xl">${totalIncome}</p>
        </div>
        <div className="bg-red-500 text-white p-4 rounded-lg text-center">
          <h2 className="text-xl font-bold w-72">Total Expenses</h2>
          <p className="text-2xl">${totalExpenses}</p>
        </div>
        <div className="bg-green-500 text-white p-4 rounded-lg text-center">
          <h2 className="text-xl font-bold w-72">Remaining Budget</h2>
          <p className="text-2xl">${remainingBudget}</p>
        </div>
      </div>
      {/* Recent Transactions */}
      <div className="flex flex-col bg-gray-100 p-4 rounded-lg mb-6"></div>
    </div>
  );
};

export default Dashboard;
