import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";

const Budget = () => {
  const [budgetText, setBudgetText] = useState("");
  const [totalBudget, setTotalBudget] = useState(0);
  const [isBudgetEditable, setIsBudgetEditable] = useState(true);
  const [message, setMessage] = useState("");
  const location = useLocation();
  const { user } = location.state || {};
  const user_id = user.user_id;

  const handleSetBudget = async () => {
    const budget = parseFloat(budgetText);
    if (isNaN(budget)) {
      setMessage("Please enter a valid budget amount.");
      return;
    }

    try {
      const response = await fetch("http://localhost:8000/home/budget/add", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ user_id, amount: budget }),
      });
      const data = await response.json();
      setMessage(data.message);
      setTotalBudget(budget);
      setIsBudgetEditable(false);
    } catch (error) {
      setMessage(`Error: ${error.message}`);
    }
  };

  const handleResetBudget = async () => {
    try {
      const response = await fetch("http://localhost:8000/home/budget/reset", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ user_id }),
      });

      if (response.ok) {
        const data = await response.json();
        setMessage(data.message);
        setTotalBudget(0);
        setBudgetText("");
        setIsBudgetEditable(true);
      } else {
        throw new Error("Failed to reset budget");
      }
    } catch (error) {
      setMessage(`Error: ${error.message}`);
    }
  };

  const fetchBudget = async () => {
    try {
      const response = await fetch(
        `http://localhost:8000/home/budget/budget/${user_id}`,
        {
          method: "GET",
          credentials: "include",
        }
      );
      console.log("fetchBudget called, user_id: ", user_id);
      console.log("fetchBudget Response: ", response);
      if (response.ok) {
        const data = await response.json();
        console.log("Budget data: ", data);
        setTotalBudget(data.amount);
        console.log("total budget: ", data.totalBudget);
      } else {
        console.error("Failed to fetch expenses data");
      }
    } catch (error) {
      console.error("Error fetching expenses data: ", error);
    }
  };

  useEffect(() => {
    if (user_id) {
      fetchBudget();
    }
  }, [user_id]);

  return (
    <div className="container mx-auto mt-8">
      <h1 className="text-5xl font-semibold text-center">Budget Planner</h1>
      <div className="mt-4">
        <label className="block text-lg font-bold">Enter Monthly Budget:</label>
        <input
          type="text"
          value={budgetText}
          onChange={(e) => setBudgetText(e.target.value)}
          className={`border rounded w-full p-8 text-center${
            isBudgetEditable ? "" : "bg-gray-200"
          }`}
          disabled={!isBudgetEditable}
          style={{ fontSize: "30px" }}
        />
      </div>
      <div className="mt-4 flex justify-between">
        <button
          onClick={handleSetBudget}
          className="bg-blue-500 text-white font-bold px-6 py-4 rounded mt-4"
          style={{ width: "150px" }}
          disabled={!isBudgetEditable}
        >
          Set Budget
        </button>

        <button
          onClick={handleResetBudget}
          className="bg-red-500 text-white font-bold px-6 py-4 rounded mt-4"
          style={{ width: "150px" }}
        >
          Reset Budget
        </button>
      </div>
      <div className="mt-4 text-red-500">{message}</div>
      <div className="flex flex-col items-center">
        {" "}
        <h2 className="text-5xl font-bold text-center mt-8"> Total Budget</h2>
        <h2 className="text-7xl font-bold text-center m-5"> {totalBudget}</h2>
      </div>
    </div>
  );
};

export default Budget;
