import React, { useState } from "react";

const Budget = () => {
  // State for budget amount
  const [budgetAmount, setBudgetAmount] = useState("");

  // State for budget categories
  const [budgetCategories, setBudgetCategories] = useState([
    { name: "Food", amount: "" },
    { name: "Transportation", amount: "" },
    { name: "Utilities", amount: "" },
    { name: "Entertainment", amount: "" },
    { name: "Education", amount: "" },
  ]);

  // Function to handle changes in budget amount
  const handleBudgetAmountChange = (e) => {
    setBudgetAmount(e.target.value);
  };

  // Function to handle changes in budget category amounts
  const handleCategoryAmountChange = (e, index) => {
    const updatedCategories = [...budgetCategories];
    updatedCategories[index].amount = e.target.value;
    setBudgetCategories(updatedCategories);
  };

  // Function to handle submission of budget
  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you can handle submitting the budget data
    console.log("Budget submitted:", { budgetAmount, budgetCategories });
  };

  return (
    <div className="container mx-auto mt-8">
      <h1 className="text-3xl font-bold mb-4">Budget</h1>
      <form onSubmit={handleSubmit}>
        {/* Input for total budget amount */}
        <div className="mb-4">
          <label className="block mb-2 font-semibold">
            Total Budget Amount
          </label>
          <input
            type="number"
            className="border border-gray-300 rounded-md py-2 px-4 w-full"
            placeholder="Enter total budget amount"
            value={budgetAmount}
            onChange={handleBudgetAmountChange}
          />
        </div>

        {/* Inputs for budget categories */}
        <div>
          <label className="block mb-2 font-semibold">Budget Categories</label>
          {budgetCategories.map((category, index) => (
            <div key={index} className="mb-2">
              <input
                type="text"
                className="border border-gray-300 rounded-md py-2 px-4 w-2/3 mr-4"
                placeholder="Category"
                value={category.name}
                disabled
              />
              <input
                type="number"
                className="border border-gray-300 rounded-md py-2 px-4 w-1/3"
                placeholder="Amount"
                value={category.amount}
                onChange={(e) => handleCategoryAmountChange(e, index)}
              />
            </div>
          ))}
        </div>

        {/* Submit button */}
        {/* <button
          type="submit"
          className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded mt-4"
        >
          Save Budget
        </button> */}
      </form>
    </div>
  );
};

export default Budget;
